#Create netflix database
CREATE DATABASE netflix;

#Use netflix database
USE netflix;

#Create Tables (In order)

CREATE TABLE MEMBER (
	Member_ID int NOT NULL,
	First_Name varchar(30),
	Last_Name varchar(30),
	Email varchar(50) NOT NULL,
	CONSTRAINT Member_PK PRIMARY KEY (Member_ID)
);

CREATE TABLE PAYMENT (
	Payment_ID bigint NOT NULL,
	Payment_Type varchar(30),
	Provider varchar(30),
	Payment_Date date,
	CONSTRAINT PAYMENT_PK PRIMARY KEY (Payment_ID)
);

CREATE TABLE SUBSCRIPTION_PLAN (
	Plan_Type varchar(30) NOT NULL,
	Price int,
	CONSTRAINT SUBSCRIPTION_PLAN_PK PRIMARY KEY (Plan_Type)
);

CREATE TABLE CONTENT (
	Content_ID varchar(30) NOT NULL,
    Content_Name varchar(255),
	Date_of_Release date,
	Maturity_Rating varchar(30),
	Description text,
	Content_Type varchar(30),
	CONSTRAINT CONTENT_PK PRIMARY KEY (Content_ID)
);

CREATE TABLE ACTOR (
	Actor_ID varchar(30) NOT NULL,
	First_Name varchar(30),
	Last_Name varchar(30),
	CONSTRAINT ACTOR_PK PRIMARY KEY (Actor_ID)
);

CREATE TABLE SERIES (
	Series_ID varchar(30) NOT NULL,
	Name varchar(30),
	CONSTRAINT SERIES_PK PRIMARY KEY (Series_ID)
);

CREATE TABLE SEASON (
	Series_ID varchar(30),
	SeasonNo int NOT NULL,
	CONSTRAINT SEASON_PK PRIMARY KEY (Series_ID, SeasonNo),
	CONSTRAINT SEASON_FK FOREIGN KEY (Series_ID) REFERENCES SERIES (Series_ID)
);

CREATE TABLE FILM (
	F_Content_ID varchar(30),
	Run_Time time,
	CONSTRAINT FILM_PK PRIMARY KEY (F_Content_ID),
	CONSTRAINT FILM_FK FOREIGN KEY (F_Content_ID) REFERENCES CONTENT (Content_ID)
);

CREATE TABLE CONTENT_ACTOR (
	Content_ID varchar(30),
	Actor_ID varchar(30),
	CONSTRAINT CONTENT_ACTOR_PK PRIMARY KEY (Content_ID, Actor_ID),
	CONSTRAINT CONTENT_ACTOR_FK1 FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID),
	CONSTRAINT CONTENT_ACTOR_FK2 FOREIGN KEY (Actor_ID) REFERENCES ACTOR (ACTOR_ID)
);


CREATE TABLE CONTENT_CREATOR (
	Content_ID varchar(30),
	Creator_Name varchar(255),
	CONSTRAINT CONTENT_CREATOR_PK PRIMARY KEY (Content_ID),
	CONSTRAINT CONTENT_CREATOR_FK FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID)
);

CREATE TABLE CONTENT_MOOD (
	Content_ID varchar(30),
	Mood varchar(255),
	CONSTRAINT CONTENT_MOOD_PK PRIMARY KEY (Content_ID),
	CONSTRAINT CONTENT_MOOD_FK FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID)
);

CREATE TABLE CONTENT_GENRE (
	Content_ID varchar(30),
	Genre varchar(255),
	CONSTRAINT CONTENT_GENRE_PK PRIMARY KEY (Content_ID),
	CONSTRAINT CONTENT_GENRE_FK FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID)
);

CREATE TABLE PROFILE (
	Member_ID int,
	Name varchar(30) NOT NULL,
	Profile_Type varchar(30),
	CONSTRAINT PROFILE_PK PRIMARY KEY (Member_ID, Name),
	CONSTRAINT PROFILE_FK FOREIGN KEY (Member_ID) REFERENCES MEMBER(Member_ID)
);

CREATE TABLE MEMBER_SUBSCRIPTION_PAYMENT (
	Member_ID int,
	Payment_ID bigint,
	Plan_Type varchar(30),
	Start_Date date,
	End_Date date,
	CONSTRAINT MEMBER_SUBSCRIPTION_PAYMENT_PK PRIMARY KEY (Member_ID, Payment_ID, Plan_Type),
	CONSTRAINT MEMBER_SUBSCRIPTION_PAYMENT_FK1 FOREIGN KEY (Member_ID) REFERENCES MEMBER (Member_ID),
	CONSTRAINT  MEMBER_SUBSCRIPTION_PAYMENT_FK2 FOREIGN KEY (Payment_ID) REFERENCES PAYMENT (Payment_ID),
	CONSTRAINT  MEMBER_SUBSCRIPTION_PAYMENT_FK3 FOREIGN KEY (Plan_Type) REFERENCES SUBSCRIPTION_PLAN (Plan_Type)
);

CREATE TABLE DEVICE (
	Device_ID int NOT NULL, 
	Member_ID int,
	Device_Type varchar(255),
	Login_Date date,
	CONSTRAINT DEVICE_PK PRIMARY KEY (Device_ID), 
	CONSTRAINT DEVICE_FK FOREIGN KEY(Member_ID) REFERENCES Member(Member_ID)
);

CREATE TABLE RECOMMENDATION (
	Member_ID int,
	Profile_Name varchar(30),
	Content_ID varchar(30),
	Score int,
	CONSTRAINT RECOMMENDATION_PK PRIMARY KEY (Member_ID, Profile_Name, Content_ID),
	CONSTRAINT RECOMMENDATION_FK1 FOREIGN KEY (Member_ID, Profile_Name) REFERENCES PROFILE (Member_ID, Name),
	CONSTRAINT RECOMMENDATION_FK2 FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID)
);

CREATE TABLE WATCH_HISTORY (
	Content_ID varchar(30),
	Member_ID int,
	Profile_Name varchar(30),
	Date_Watched date,
	Duration_Watched time,
	CONSTRAINT WATCH_HISTORY_PK PRIMARY KEY (Content_ID, Member_ID, Profile_Name),
	CONSTRAINT WATCH_HISTORY_FK1 FOREIGN KEY (Content_ID) REFERENCES CONTENT (Content_ID),
	CONSTRAINT WATCH_HISTORY_FK2 FOREIGN KEY (Member_ID, Profile_Name) REFERENCES PROFILE (Member_ID, Name)
);

CREATE TABLE EPISODE (
	E_Content_ID varchar(30),
	Series_ID varchar(30),
	SeasonNo int,
	EpisodeNo int,
	CONSTRAINT EPISODE_PK PRIMARY KEY (E_Content_ID),
	CONSTRAINT EPISODE_FK1 FOREIGN KEY (E_Content_ID) REFERENCES CONTENT (Content_ID),
	CONSTRAINT EPISODE_FK2 FOREIGN KEY (Series_ID, SeasonNo) REFERENCES SEASON (Series_ID, SeasonNo)
);

