-- FUNCTIONAL QUERIES

#TOP 5 SERIES WATCHED IN 2024
SELECT s.name, year(date_watched) AS year, count(*) AS view_count
FROM watch_history w
INNER JOIN content c ON w.content_id = c.content_id
INNER JOIN episode e ON w.content_id = e.e_content_id
INNER JOIN series s ON s.series_id = e.series_id
WHERE content_type = 'Episode'
AND year(date_watched) = 2024
GROUP BY s.name, year
ORDER BY view_count DESC
LIMIT 5;


#Monthly Revenue by Subscription Plan
SELECT sp.plan_type, year(start_date) AS year, month(mps.start_date) AS month,
SUM(sp.price) AS total_revenue, count(*) AS total_users
FROM member_subscription_payment mps 
INNER JOIN subscription_plan sp ON mps.plan_type = sp.plan_type 
AND month(mps.start_date) = 10 
AND year(start_date) = 2024 
GROUP BY sp.plan_type, year, month
ORDER BY total_revenue DESC;

#DEVICES
#Context: Basic = 1, Standard = 2, Premium 4

#Active Devices per Member
SELECT d.member_id, msp.plan_type, count(distinct(device_id)) as device_count
FROM device d
INNER JOIN MEMBER_SUBSCRIPTION_PAYMENT msp on msp.member_id = d.member_id
AND d.member_id in 
(#Members with Active Subscription
	SELECT member_id from MEMBER_SUBSCRIPTION_PAYMENT
	WHERE CURRENT_DATE between start_date and end_date)
GROUP BY d.member_id, msp.plan_type
ORDER BY plan_type;

#Members that are likely to Churn
#Inactive member(s) that have subscribed for at least 3 week
SELECT member_id as inactive_members
FROM (SELECT member_id
	FROM MEMBER_SUBSCRIPTION_PAYMENT
	WHERE CURRENT_DATE BETWEEN start_date AND end_date
    AND DATEDIFF(CURRENT_DATE, start_date) > 21) AS active_subscriptions
WHERE member_id NOT IN
(SELECT DISTINCT member_id FROM watch_history);
